var TOTAL_TIME = 40;
var SMALL_SCALE = 0.5;
var BIG_SCALE = 0.7;
var PAO_LAYER_Y;
var CAT_DOG_Y;

var LOCAL_STORAGE_OK = true;

function getQueryString(name) {
	var reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)", "i");
	var r = window.location.search.substr(1).match(reg);
	if (r != null) return unescape(r[2]); return null;
}

var g_playScene = null;
var g_playLayer = null;
function showUserInfo(info){
	g_playLayer.showUserInfo(info);
}
