	restart:function(){
		playMusic(res.bg_mp3);
		this.state = "wait";
		this.score = 0;
		this.oneScore = 10;
		this.multiple = 1;

		var highScore = null;
		var ls = cc.sys.localStorage;
		if(LOCAL_STORAGE_OK)
			highScore = ls.getItem("highScore");
		else
			highScore = 0;
		if(highScore == null) highScore = 0;
		this.highScoreLabel.setString(""+highScore);
		
		this.scoreLabel.setString("0");
		this.time = TOTAL_TIME;
		this.timeBar.setPercentage(100);
		this.timeLabel.setString(""+TOTAL_TIME);
		this.stopTime = 0;
		this.level = 0;
		this.paopaoLayer.clear();
		this.spineCat.setVisible(true);
		this.spineDog.setVisible(true);
		this.spineCat.setScale(SMALL_SCALE);
		this.spineDog.setScale(SMALL_SCALE);
		this.spineCat.setPosition(cc.winSize.width/2+140,CAT_DOG_Y);
		this.spineDog.setPosition(cc.winSize.width/2-140,CAT_DOG_Y);
		this.uiComeIn();
		this.enableButton(false);
		this.start();
	},
	start:function(){
		this.state = "wait";
		this.ready();
		// 游戏中计时
		//this.scheduleUpdate();
		this.schedule(this.update, 0.1);
		//this.startSchedule();
	},
